import axios from "axios";
import "./css/cars.css";
import React, { useEffect } from "react";
import Header from "../components/Header";
import VehicleList from "../components/VehicleList";
import Select from "react-select";
import { Button } from "@mui/material";

const typeOptions = [
  { value: "coupe", label: "Coupe" },
  { value: "sedan", label: "Sedan" },
  { value: "truck", label: "Truck" },
  { value: "suv", label: "SUV" },
  { value: "mini van", label: "Mini-Van" },
  { value: "convertable", label: "Convertable" },
];

const seatsOptions = [
  { value: 2, label: "2 Seats" },
  { value: 4, label: "4 Seats" },
  { value: 5, label: "5 Seats" },
  { value: 7, label: "7 Seats" },
  { value: 8, label: "8 Seats" },
];

const priceOptions = [
  { value: "4000-6000", label: "₹4000 - ₹6000" },
  { value: "6100-8000", label: "₹6100 - ₹8000" },
  { value: "8100-10000", label: "₹8100 - ₹10000" },
];

export default function Cars() {
  const [cars, setCars] = React.useState([]);

  const [params, setParams] = React.useState({});

  useEffect(() => {
    axios.get("http://localhost:5000/bikes").then((res) => {
      setCars(res.data);
      console.log(res.data);
    });
  }, []);

  const filterCars = () => {
    console.log(params);
    axios
      .get("http://localhost:5000/bikes", {
        params: params,
      })
      .then((res) => {
        setCars(res.data);
        console.log(res.data);
      });
  };

  const handleTypeChange = (data) => {
    if (data == null) {
      setParams({ ...params, type: null });
      return;
    }
    setParams({
      ...params,
      type: data.value,
    });
  };

  const handleSeatChange = (data) => {
    if (data == null) {
      setParams({ ...params, seats: null });
      return;
    }
    setParams({
      ...params,
      seats: data.value,
    });
  };

  const handlePriceChange = (data) => {
    if (data == null) {
      setParams({ ...params, price: null });
      return;
    }
    setParams({
      ...params,
      price: data.value,
    });
  };

  return (
    <div>
      <Header pageName="Cars" />
      <div className="container" style={{ zIndex: 2, marginTop: "40px" }}>
        <div className="filter">
          <Select
            name="type"
            placeholder={<div>Type</div>}
            onChange={handleTypeChange.bind(this)}
            isClearable={true}
            options={typeOptions}
          />
          <Select
            name="seat"
            placeholder={<div>Select Number of Seats</div>}
            onChange={handleSeatChange.bind(this)}
            isClearable={true}
            options={seatsOptions}
          />
          <Select
            name="price"
            placeholder={<div>Select Price Range</div>}
            onChange={handlePriceChange.bind(this)}
            isClearable={true}
            options={priceOptions}
          />
        </div>
        <div className="filter">
          <Button
            variant="contained"
            onClick={() => filterCars()}
            color="success"
            style={{ marginTop: "20px" }}
          >
            Filter
          </Button>
        </div>

        <VehicleList cars={cars} />
      </div>
    </div>
  );
}
